import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';
import { useState } from 'react';


function App() {
  const [isEditProfilePopupOpen,setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen,setisAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen,setisEditAvatarPopupOpen] = useState(false);

  function openPopup (popup) { 

    popup.classList.add("popup_active"); 

}; 

function handleEditAvatarClick () {
    const editAvatar = document.querySelector(".popup_avatar")
    openPopup(editAvatar);
}

function handleEditProfileClick () {
    const editProfile = document.querySelector('.popup_edit')
    openPopup(editProfile);
}

function handleAddPlaceClick () {
    const addPlace = document.querySelector('.popup_elements')
    openPopup(addPlace);
}

  return (
    <div className="App">
        <head>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>Mesto</title>
  </head>
  <body class="page">
  <Header />
    <Main 
    onEditAvatar={handleEditAvatarClick}
    onEditProfile ={handleEditProfileClick} 
    onAddPlace={handleAddPlaceClick}
     />
    <Footer />
    <PopupWithForm />
    <ImagePopup />
    <template id="cardTemplate">
      <div class="element">
        <img class="element__image" src="image" alt="Картинка" />
        <button type="button" class="element__basket"></button>
        <div class="element__text">
          <h2 class="element__title"></h2>
          <div class="element__likes">
            <button type="button" class="element__heart"></button>
            <p class="element__like-count"></p>
          </div>
        </div>
      </div>
    </template>
    </body> 
    </div>
  );
}

export default App;
