import React, { useState } from 'react';
import  api  from '../utils/Api.js';

function Main(props) {

    const [userName, setUserName] = useState();
    const [userDescription, setUserDescription] = useState();
    const [userAvatar, setUserAvatar] = useState();

    React.useEffect (() => {
        Promise.all([api.getProfileData(), api.getInitialCards()])
  .then(([profile, cards]) => {
    setUserName(profile.name);
    setUserDescription(profile.about);
    setUserAvatar(profile.avatar);
    cards.forEach((card) => {
      card.canDelete = card.owner._id == profile._id;
      card.iLike = false;
      card.likes.forEach((like) => {
        if (like._id === profile._id) {
          card.iLike = true;
        }
      });
    });
    defaultCardList.renderItems(cards);
  })
  .catch((err) => {
    console.log(`Ошибка; ${err}`);
  });
    })

    return (
        <>
    <main>
      <section className="profile">
        <div className="profile__info">
          <button type="submit" className="profile__button-change">
            <img
              className="profile__avatar"
              src={userAvatar}
              alt="Аватар" onClick={props.onEditAvatar}
            />
            <img
              className="profile__pencil"
              src="<%=require('./images/pencil-big.svg')%>"
              alt="Карандаш" 
            />
          </button>
          <div className="profile__text">
            <div className="profile__edit">
              <h1 className="profile__title">{userName}</h1>
              <button type="button" className="profile__button-add" onClick={props.onEditProfile}></button>
            </div>
            <p className="profile__subtitle">{userDescription}</p>
          </div>
        </div>
        <button className="profile__button-edit" onClick={props.onAddPlace}></button>
      </section>
      <section className="cards"></section>
    </main>
        <template id="cardTemplate">
        <div class="element">
          <img class="element__image" src="image" alt="Картинка" />
          <button type="button" class="element__basket"></button>
          <div class="element__text">
            <h2 class="element__title"></h2>
            <div class="element__likes">
              <button type="button" class="element__heart"></button>
              <p class="element__like-count"></p>
            </div>
          </div>
        </div>
      </template>
      </>
    );
  }

  export default Main;


