import React from "react";
import { Link } from "react-router-dom";
import InfoTooltip from "./InfoTooltip";
import * as auth from "../auth.js";
import iconSuccess from "../images/Union.svg";
import iconDenied from "../images/denied.svg";

class Register extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
      tipOpen: false,
      isSuccess: false,
      errorMessage: null,
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange = (e) => {
    const { name, value } = e.target;
    this.setState({
      [name]: value,
    });
  };
  handleSubmit = (e) => {
    e.preventDefault();
    // здесь обработчик регистрации
    auth.register(this.state.password, this.state.email).then((res) => {
      if (res) {
        this.setState({
          errorMessage: res.error ? res.error : null,
          tipOpen: true,
          isSuccess: !res.error,
        });

        // if (res.error) {
        //   this.setState({
        //     errorMessage: res.error,
        //     tipOpen: true,
        //     isSuccess: false,
        //   });
        // } else {
        //   this.setState({
        //     errorMessage: null,
        //     tipOpen: true,
        //     isSuccess: true,
        //   });
        // }


      } else {
        this.setState({
          errorMessage: "Что-то пошло не так! Попробуйте ещё раз.",
          tipOpen: true,
          isSuccess: false,
        });
      }
    });
  };

  handleTipClose() {
    this.setState({ tipOpen: false }, () => {
      if (this.state.isSuccess) {
        this.props.onRegSuccess();
      }
    });
  }

  render() {
    return (
      <>
        <div className="register">
          <p className="register__welcome login__welcome">Регистрация</p>
          <form
            onSubmit={this.handleSubmit}
            className="register__form login__form"
          >
            <label htmlFor="email">Email:</label>
            <input
              className="login__input"
              placeholder="Email"
              id="email"
              name="email"
              type="email"
              value={this.state.email}
              onChange={this.handleChange}
            />
            <label htmlFor="password">Пароль:</label>
            <input
              className="login__input"
              placeholder="Пароль"
              id="password"
              name="password"
              type="password"
              value={this.state.password}
              onChange={this.handleChange}
            />
            <div className="register__button-container">
              <button
                type="submit"
                onSubmit={this.handleSubmit}
                className="register__link login__link"
              >
                Зарегистрироваться
              </button>
            </div>
          </form>
          <div className="register__signin">
            <Link to="/signin" className="register__login-link">
              Уже зарегистрированы? Войти
            </Link>
          </div>
        </div>
        <InfoTooltip
          isOpen={this.state.tipOpen}
          name="tip "
          onClose={() => this.handleTipClose()}
        >
          <img
            className="popup__icon"
            src={this.state.isSuccess ? iconSuccess : iconDenied}
            alt={this.state.isSuccess ? "Успех" : "Неудача"}
          />
          <p className="popup__icon-title">
            {this.state.isSuccess
              ? "Вы успешно зарегистрировались!"
              : this.state.errorMessage
              }
          </p>
        </InfoTooltip>
      </>
    );
  }
}

export default Register;
