import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
import ImagePopup from "./ImagePopup";
import React, { useState } from "react";
import api from "../utils/Api.js";
import Card from "./Card";
import { CurrentUserContext } from "./CurrentUserContext";
import EditProfilePopup from './EditProfilePopup';
import EditAvatarPopup from "./EditAvatarPopup";


function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setisAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setisEditAvatarPopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState(null);
  const [currentUser, setCurrentUser] = useState('');
  const [cards, setCards] = useState([]);
  function closeAllPopups() {
    setisEditAvatarPopupOpen(false);
    setisAddPlacePopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setSelectedCard(null);
  }

  function handleCardClick(selectedCard) {
    setSelectedCard(selectedCard);
  }

  function handleUpdateUser(data) {
    api.setProfileData(data)
    .then((profile) => {
      setCurrentUser(profile);
      closeAllPopups() ;
    })
  }

  function handleUpdateAvatar(data) {
    api.setUserAvatar(data)
    .then((profile) => {
      setCurrentUser(profile);
      closeAllPopups() ;
    })
  }

  function handleCardLike(card) {
    const isLiked = card.likes.some(i => i._id === currentUser._id);
    api.changeLikeCardStatus(card._id, isLiked).then((newCard) => {
      const newCards = cards.map((c) => c._id === card._id ? newCard : c);
      setCards(newCards);
    });
  }

  function handleCardDelete(card) {
      api.deleteCard(card._id). then((newCard) => {	
      const newCards = cards.filter((c) => c._id === card._id ? newCard : c);
      setCards(newCards);
    });
  }

  React.useEffect(() => {
    Promise.all([api.getInitialCards()])
      .then(([cards]) => {
        setCards(cards);
      })
      .catch((err) => {
        console.log(`Ошибка; ${err}`);
      });
  }, []);

  React.useEffect(() => {
    Promise.all([api.getProfileData()])
      .then(([profile]) => {
        setCurrentUser(profile);
      })
      .catch((err) => {
        console.log(`Ошибка; ${err}`);
      });
  }, []);



  return (
    <div className="App">
      <CurrentUserContext.Provider value={currentUser}>
      <Header />
      <Main
        onEditAvatar={() => setisEditAvatarPopupOpen(true)}
        onEditProfile={() => setIsEditProfilePopupOpen(true)}
        onAddPlace={() => setisAddPlacePopupOpen(true)}
        onCardClick={(selectedCard) => handleCardClick(selectedCard)}
        onCardLike={handleCardLike}
        onCardDelete={handleCardDelete}
      />

      <Footer />
      <EditProfilePopup isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} onUpdateUser={handleUpdateUser} />

      <PopupWithForm
        name="popup_elements"
        title="Новое место"
        submitname="Создать"
        isOpen={isAddPlacePopupOpen}
        onClose={() => closeAllPopups(true)}
      >
        <fieldset className="popup__fieldset">
          <input
            type="text"
            name="name"
            id="imgname"
            placeholder="Название"
            minLength="2"
            maxLength="30"
            className="popup__profile popup__input popup__profile_title popup__profile_elements popup__profile_title_elements"
            required
          />
          <span id="imgname-error" className="popup__input-error"></span>
        </fieldset>
        <fieldset className="popup__fieldset">
          <input
            type="url"
            name="link"
            id="imglink"
            placeholder="Ссылка на картинку"
            className="popup__profile popup__input popup__profile_subtitle popup__profile_elements popup__profile_subtitle_elements"
            required
          />
          <span id="imglink-error" className="popup__input-error"></span>
        </fieldset>
      </PopupWithForm>

      <EditAvatarPopup isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} onUpdateAvatar={handleUpdateAvatar} />

      {/* 
    <PopupWithForm 
    name='popup_sure' 
    title='Обновить Вы уверены?' 
    submitname='Да'
    isOpen={}
    /> */}
      <ImagePopup card={selectedCard} onClose={() => closeAllPopups(true)} />
      </CurrentUserContext.Provider>

    </div>
  );
}

export default App;
