import React, { useState } from "react";
import api from "../utils/Api.js";
import Card from "./Card";
import pencil from "../images/pencil.svg";
import { CurrentUserContext } from "../components/CurrentUserContext";

function Main(props) {
  const [cards, setCards] = useState([]);
  const currentUser = React.useContext(CurrentUserContext);

  React.useEffect(() => {
    Promise.all([api.getInitialCards()])
      .then(([cards]) => {
        // cards.forEach((card) => {
        //   card.canDelete = card.owner._id == profile._id;
        //   card.iLike = false;
        //   card.likes.forEach((like) => {
        //     if (like._id === profile._id) {
        //       card.iLike = true;
        //     }
        //   });
        // });
        setCards(cards);
      })
      .catch((err) => {
        console.log(`Ошибка; ${err}`);
      });
  });

function handleCardLike(card) {
	// Снова проверяем, есть ли уже лайк на этой карточке
	const isLiked = card.likes.some(i => i._id === currentUser._id);

	// Отправляем запрос в API и получаем обновлённые данные карточки
	api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
		// Формируем новый массив на основе имеющегося, подставляя в него новую карточку
	//   const newCards = cards.map((c) => c._id === card._id ? newCard : c);
    props.onSetCards((state) => state.map((c) => c._id === card._id ? newCard : c))
	  // Обновляем стейт
	  setCards(newCards);
	});
}

  return (
    <main>
      <section className="profile">
        <div className="profile__info">
          <button type="submit" className="profile__button-change">
            <img
              className="profile__avatar"
              src={currentUser?.avatar}
              alt="Аватар"
              onClick={props.onEditAvatar}
            />
            <img className="profile__pencil" src={pencil} alt="Карандаш" />
          </button>
          <div className="profile__text">
            <div className="profile__edit">
              <h1 className="profile__title">{currentUser?.name}</h1>
              <button
                type="button"
                className="profile__button-add"
                onClick={props.onEditProfile}
              ></button>
            </div>
            <p className="profile__subtitle">{currentUser?.about}</p>
          </div>
        </div>
        <button
          className="profile__button-edit"
          onClick={props.onAddPlace}
        ></button>
      </section>
      <section className="cards">
        {cards.map((card) => (
          <Card card={card} id={currentUser._id} onCardClick={props.onCardClick} onCardLike={handleCardLike}/>
        ))}
      </section>
    </main>
  );
}
export default Main;
